mapper.select:
select * from TOP_BANKINFO
where 1=1

	and BANK_CODE = #{bankCode,jdbcType=VARCHAR}


	and BANK_NAME = #{bankName,jdbcType=VARCHAR}


	and BANK_LEVEL = #{bankLevel,jdbcType=VARCHAR}


	and UP_BANKCODE = #{upBankcode,jdbcType=VARCHAR}


	and ADMIN_FLAG = #{adminFlag,jdbcType=VARCHAR}


	and ADDRESS = #{address,jdbcType=VARCHAR}


	and POSTCODE = #{postcode,jdbcType=VARCHAR}


	and PHONE = #{phone,jdbcType=VARCHAR}


	and FAX = #{fax,jdbcType=VARCHAR}


	and STATUS = #{status,jdbcType=VARCHAR}


	and OPERATOR_BANKCODE =
	#{operatorBankcode,jdbcType=VARCHAR}


	and OPERATOR_CODE = #{operatorCode,jdbcType=VARCHAR}


	and UPDATEDATE = #{updatedate,jdbcType=VARCHAR}


	and UPDATETIME = #{updatetime,jdbcType=VARCHAR}


	and CERTNO = #{certno,jdbcType=VARCHAR}


	and CERTENDDATE = #{certenddate,jdbcType=VARCHAR}


	and CERTSTARTDATE = #{certstartdate,jdbcType=VARCHAR}
